/// <reference types="node" />
import { Transform, TransformCallback } from 'stream';
import { Writer } from 'protobufjs';
/**
 * Transforms a stream of protobuf messages to bytes
 */
export default class ProtobufBinaryStream<T> extends Transform {
    private readonly encodeDelimited;
    constructor(encodeDelimited: (message: T) => Writer);
    _transform(message: T, encoding: string, callback: TransformCallback): void;
}
